import "./App.css";
import Navbar from "./components/Navbar";

import { useState } from "react";
import { BrowserRouter, Route, Routes, Link } from 'react-router-dom';
import Chess from "./Maincontent/Chess/Chess";
import FootBall from "./Maincontent/FootBall/FootBall";
import VolleyBall from "./Maincontent/VolleyBall/VolleyBall";
import TableTennis from "./Maincontent/TableTennis/TableTennis";
import Rugby from "./Maincontent/Rugby/Rugby";
import { Login } from "./Login/Login";
import Home from "./Maincontent/Home/Home";






function App() {
    const [log, setLog] = useState(localStorage.getItem('Loginsuccess'));
    const Logout = () => {
        localStorage.removeItem('Loginsuccess');


        window.location.reload();
        window.location = "/";
    }
    return (

        <div className="App">
            <BrowserRouter>

                <div className="App">
                    <div className="nav">.

                        {!log && (

                            <>
                                <ul>
                                    <h3>CODINGMARKET</h3>



                                </ul>
                            </>

                        )}
                        {log && (

                            <>


                                <IconContext.Provider value={{ color: "eeeeee" }}>
        <div className="navbar">
          <header className="header">
            <h3>CODINGMARKET</h3>
          </header>
          <Link to="#" className="menuBars">
            <FaIcons.FaBars onClick={showSidebar} />
          </Link>

        </div>

        <div className='menu-container' ref={menuRef}>
          <div className='menu-trigger' onClick={() => { setOpen(!open) }}>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzNg8Qf5iIU9m4e7qT6Zuv82CU3FqXYOoESRdPukzzgw&s.jpg" alt="React Image" />

          </div>
          <div>
            <div id="login-time">Logged in since: </div>
            <div id="timer">Time elapsed: 00:00:00</div>

          </div>

          <div className={`dropdown-menu ${open ? 'active' : 'inactive'}`} >
            <h3>chandru R<br /><span></span></h3>
            <ul>
              <li className='dropdownItem' onClick={() => console.log('My Profile clicked')}>
                <a>My Profile</a>
              </li>
              <li className='dropdownItem' onClick={() => console.log('Edit Profile clicked')}>
                <a>Edit Profile</a>
              </li>
              <li className='dropdownItem' onClick={() => console.log('Inbox clicked')}>
                <a>Inbox</a>
              </li>
              <li className='dropdownItem' onClick={handleLogout}>
                <a>Logout</a>
              </li>
            </ul>
          </div>
        </div>

        <nav className={sidebar ? "navMenu active" : "navMenu"}>
          <ul className="navMenuItems"  >
            {/* onClick={showSidebar} */}
            <li className="navbarToggle">
              <Link to="#" className="menuBars">

              </Link>
            </li>
            {SidebarData.map((item, index) => {
              return (
                <li key={index} className={item.className}>
                  <Link to={item.path}>
                    {item.icon}
                    <span>{item.title}</span>
                  </Link>
                </li>

              );
            })}
          </ul>
        </nav>
      </IconContext.Provider>









                            </>
                        )}

                    </div>




                    <Routes>


                        <Route path="/Chess" exact element={<Chess />} />
                        <Route path="/FootBall" element={<FootBall />} />
                        <Route path="/TableTennis" element={<TableTennis />} />

                        <Route path="/Volleyball" element={<VolleyBall />} />
                        <Route path="/Rugby" element={<Rugby />} />
                        <Route path="/login" element={<Login />} />

                    </Routes>












                </div>

            </BrowserRouter>
        </div>








    );
}

export default App;
