import { Link } from "react-router-dom";
import * as FaIcons from "react-icons/fa";
import * as MdIcons from "react-icons/md";
import { SidebarData } from "./SidebarData";
import { IconContext } from "react-icons";
import { NavLink, Routes, Route } from 'react-router-dom';


import "./Navbar.css";
import React, { useState, useEffect } from 'react';


function Navbar() {
  const [sidebar, setSidebar] = useState(false);
  const [loginTime, setLoginTime] = useState(new Date());
  const [currentTime, setCurrentTime] = useState(new Date());




  const showSidebar = () => {
    setSidebar(!sidebar);

  };




  const [open, setOpen] = useState(false);

  let menuRef = useRef();
  useEffect(() => {
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);
  useEffect(() => {
    let handler = (e) => {
      if (!menuRef.current.contains(e.target)) {
        setOpen(false);
        console.log(menuRef.current);
      }
    };

    document.addEventListener("mousedown", handler);




    const getLoggedInTime = () => {
      const diff = Math.abs(currentTime - loginTime);
      return Math.floor(diff / 1000);
    };

    const handleLogout = () => {
      setLoginTime(new Date());
      setCurrentTime(new Date());
      // perform logout logic
    };
    return () => {

      document.removeEventListener("mousedown", handler);
    }

  })



  return (
    <>
      <IconContext.Provider value={{ color: "eeeeee" }}>
        <div className="navbar">
          <header className="header">
            <h3>CODINGMARKET</h3>
          </header>
          <Link to="#" className="menuBars">
            <FaIcons.FaBars onClick={showSidebar} />
          </Link>

        </div>

        <div className='menu-container' ref={menuRef}>
          <div className='menu-trigger' onClick={() => { setOpen(!open) }}>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRzNg8Qf5iIU9m4e7qT6Zuv82CU3FqXYOoESRdPukzzgw&s.jpg" alt="React Image" />

          </div>
          <div>
            <p>Login Time: {loginTime.toLocaleString()}</p>
            <p>Current Time: {currentTime.toLocaleString()}</p>


          </div>


          <div className={`dropdown-menu ${open ? 'active' : 'inactive'}`} >
            <h3>chandru R<br /><span></span></h3>
            <ul>              <DropdownItem text={"My Profile"} />
              <DropdownItem text={"Edit Profile"} />
              <DropdownItem text={"Inbox"} />

              <button onClick={handleLogout}>Logout</button>

            </ul>
          </div>
        </div>

        <nav className={sidebar ? "navMenu active" : "navMenu"}>
          <ul className="navMenuItems"  >
            {/* onClick={showSidebar} */}
            <li className="navbarToggle">
              <Link to="#" className="menuBars">

              </Link>
            </li>
            {SidebarData.map((item, index) => {
              return (
                <li key={index} className={item.className}>
                  <Link to={item.path}>
                    {item.icon}
                    <span>{item.title}</span>
                  </Link>
                </li>

              );
            })}
          </ul>
        </nav>
      </IconContext.Provider>
    </>
  );
}

function DropdownItem(props) {
  return (
    <div>

      <li className='dropdownItem'>

        {/* <img src={props.img}></img> */}

        <a> {props.text} </a>

      </li>
    </div>
  );
}
export default Navbar;
