import logo from './logo.svg';

import './App.css';
import { NavLink, Routes, Route } from 'react-router-dom'

import { useState, useEffect } from 'react'


import Chess from './Chess';
import VolleyBall from './VolleyBall';




import Home from './Maincontent/Home/Home';
import FootBall from './Maincontent/FootBall/FootBall';





function App() {

  var array = ["none", "none"]

  const [state, setstate] = useState(array);


  useEffect(() => {
    window.addEventListener('popstate', () => {

      if (window.location.pathname === "/Home") {
        var array1 = ["none", "none"]
        setstate(array1);
      }

      else if (window.location.pathname === "/Home/Chess" || window.location.pathname === "/Home/VolleyBall" || window.location.pathname === "/Home/FootBall") {
        var array1 = ["block", "none"]
        setstate(array1);
      }

      else if (window.location.pathname === "/Home/TableTennis" || window.location.pathname === "/Home/Rugby") {
        var array1 = ["none", "block"]
        setstate(array1);
      }




    })
  }, [])



  function myfun() {

    var array1 = ["none", "none"]
    setstate(array1);
  }


  return (
    <div>

      <header className="header">
        <NavLink className="styling" style={({ isActive }) => ({
          color: isActive ? 'skyblue' : 'blue'
        })} onClick={myfun} to="/">CODINGMARKET</NavLink>


      </header>

      <Routes>

        <Route path="/" element={<Home state={state} setstate={setstate} />}>


          <Route path="Chess" element={<Chess />}></Route>
          <Route path="VolleyBall" element={<VolleyBall />}></Route>
          <Route path="FootBall" element={<FootBall />}></Route>


        </Route>


      </Routes>


    </div>
  );
}


export default App;
